import React from "react";

function Employee(){
    const [formData, setFormData] = useState({
            name: '',
            rollNumber: '',
            standard: '',
            date: '',
            status: 'present',
        });
    
        const handleChange = (e) => {
            const { name, value } = e.target;
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        };
    
        const handleSubmit = async (e) => {
            e.preventDefault();
    
            try {
                const response = await fetch('http://localhost:5000/submit-attendance', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(formData)
                });
    
                const result = await response.text();
                alert(result);
            } catch (error) {
                console.error('Error:', error);
                alert('Failed to submit attendance');
            }
        };
    return(
        <>
            <div className="main-div">
                <div className="form-box">
                    <form>
                        <h2>Employee details</h2>

                        <label>Name</label> <br/>
                        <input type="text" name="name" placeholder="Enter Name" required /> <br/><br/>

                        <label>Experience</label> <br/>
                        <input type="text" name="experience" placeholder="Enter Experience" required/> <br/><br/>

                        <label>Profession</label> <br/>
                        <input type="text" name="Profession" placeholder="video Editor/web developer/ etc..." required/> <br/><br/>

                        <label>Joining date</label> <br/>
                        <input type="date" name="joining_date" required/> <br/><br/>

                        <label>Salary</label> <br/>
                        <input type="number" name="salary" placeholder="Enter Salary"/> <br/> <br/>

                        <button type="submit">Submit</button>
                    </form>
                </div>
            </div>
        </>
    );
}
export default Employee;
