import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({
  apiKey: "AIzaSyABFMOfym-BkUg9i1Idn0p_JIWmlRctKfA", // replace with your actual key
});

export async function runChat(prompt) {
  const model = ai.getGenerativeModel({ model: "gemini-1.5-pro" }); // Or your model

  const result = await model.generateContent([prompt]);

  const response = await result.response;

  const text = response.text(); // ✅ this extracts the whole reply
  return text;
}
