import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({
  apiKey: "AIzaSyDO9TQuneGWSgwObHnMBJ3KzIikAB7ekQ0", // replace with your actual key
});

export async function runChat(prompt) {
  const tools = [
    {
      googleSearch: {},
    },
  ];

  const config = {
    thinkingConfig: {
      thinkingBudget: -1,
    },
    tools,
  };

  const model = 'gemini-2.5-pro';

  const contents = [
    {
      role: 'user',
      parts: [
        {
          text: prompt,
        },
      ],
    },
  ];

  // ✅ Use generateContent (NOT generateContentStream)
  const response = await ai.models.generateContent({
    model,
    config,
    contents,
  });

  // Extract the result
  const finalOutput = response.response.parts.map((part) => part.text).join('');
  return finalOutput;
}
